import React, { useEffect, useState } from 'react'
import axios from 'axios'; //thư viện HTTP client thực hiện CRUD

const StudentsView = () => {
  //Sử dụng Hook useState trong React tạo một state có tên là students và 
  //một hàm setter có tên là setStudents cập nhật giá trị cho state
  const [students, setStudents] = useState([]); 

  useEffect(() => {
    loadStudents();
  }, [])

  const loadStudents = async() => {
    const result = await axios.get("http://localhost:9091/students/getall",{
      validateStatus: () => {
        return true;
      }
    }
    );
    if (result.sta){
      setStudents(result);
    }
  };

  return (
    <section>
      <table>

        <thead>
          <tr>
            <th>ID</th>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Email</th>
            <th>Age</th>
            <th>Phone Number</th>
            <th>Department</th>
          </tr>
        </thead>

        <tbody>
          {students.map((student, index) => (
            <tr key={student.id}>
              <th scope='row' key={index}>
                {index+1}
              </th>
              <td>{student.firstName}</td>
              <td>{student.lastName}</td>
              <td>{student.email}</td>
              <td>{student.age}</td> 
              <td>{student.phoneNumber}</td>
              <td>{student.department}</td>
              <td>View</td>
              <td>Update</td>
              <td>Delete</td>
            </tr>
          ))}
        </tbody>

      </table>
    </section>
  )
}

export default StudentsView
